import React, {Component} from 'react';
import "./header.scss"
import logo from "./assets/a-logo.png"
import {Link} from "react-router-dom";


class Header extends Component{
    constructor(props) {
        super(props);
        this.addActiveClass= this.addActiveClass.bind(this);
        this.state = {
            active: false,
        };
    }
    toggleClass() {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
    };


    render () {
        const menuItems = [{value:"WOMEN", link:"/women"},{value:"MEN", link:"/men"},{value:"KIDS", link:"/kids"}]
        return(
        <div className="header__container">
            <div className="header__menu">
                    {menuItems.map((item, index) => (
                            <Link to={item.link}  className={ this.state.active ? "header__menu_item active" : "header__menu_item"} onClick={() => this.setState(prevState => ({
                                active: !prevState.value
                            }))} key={index}> {item.value}</Link>
                    ))}
            </div>
            <div className="header__logo"><img src={logo} alt="logo"/></div>
            <div className="header__card">
                <i className="fa-solid  fa-xl fa-dollar-sign"></i>
                <i className="fas fa-regular fa-xl fa-cart-shopping"></i>
            </div>
            </div>
        )
    }
}

export default Header
