import React, { Component } from "react";
import "./FullCard.scss";
import { graphql } from "@apollo/client/react/hoc";
//import { LOAD_PRODUCT } from "../../Graphql/Query";
import { query } from "react/app";
import { gql } from "@apollo/client";

class FullCard extends Component {
  render() {
    const LOAD_PRODUCT = gql`
  query {
    product(id:${this.props.productId}) {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        amount
        currency {
          label
          symbol
        }
      }
      brand
    }
  }
`;
    console.log(this.props);
    return (
      <div>
        <div className="fullCard__container">
          <img />
          <div className="fullCard__info">
            <div className="fullCard__name"></div>
          </div>
        </div>
      </div>
    );
  }
}

export default FullCard;
