import React, { Component } from "react";
import Header from "./components/Header/header";
import { Routes, Route } from "react-router-dom";
import FullCard from "./Pages/FullCard/FullCard";
import Main from "./Pages/Main/Main";
import "./App.scss";
import NoFound from "./Pages/NoFound/NoFound";
import Cart from "./Pages/Cart/Cart";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencies: 0,
      productId: "",
      category: 0,
      cartProduct: [],
    };
    this.updateState = this.updateState.bind(this);
    this.getProductId = this.getProductId.bind(this);
    this.getCategoryId = this.getCategoryId.bind(this);
    this.setCartProduct = this.setCartProduct.bind(this);
  }
  updateState(id) {
    this.setState({ currencies: id });
  }
  getProductId(id) {
    this.setState({ productId: id });
  }
  getCategoryId(id) {
    this.setState({ category: id });
  }
  setCartProduct(obj) {
    this.setState({ cartProduct: [...this.state.cartProduct, obj] });
  }

  render() {
    console.log(this.state.cartProduct);
    return (
      <div className="wrapper">
        <Header
          update={this.updateState}
          getCategoryId={this.getCategoryId}
          items={this.state.cartProduct}
        />
        <Routes>
          <Route
            path={"/"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={"/clothes"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={"/tech"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={`/item/:id`}
            element={
              <FullCard
                productId={this.state.productId}
                currencies={this.state.currencies}
                setCartProduct={this.setCartProduct}
              />
            }
          />
          <Route
            path="/cart"
            element={
              <Cart
                currencies={this.state.currencies}
                data={this.state.cartProduct}
              />
            }
          />
          <Route path="*" element={<NoFound />} />
        </Routes>
      </div>
    );
  }
}

export default App;
