import { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

import { getProducts, getTotalPrice } from "../../redux/cart/cart_selector";

import styles from "./CartProduct.module.css";
import routes from "../../routes";
import Item from "../Item";

class CartProduct extends Component {
  state = {
    quantity: 1,
  };

  handleAttributes = () => {
    //  alert('Selected');
  };

  increment = () => {
    this.setState((state) => {
      return {
        quantity: (state.quantity += 1),
      };
    });
  };

  decrement = () => {
    this.setState((state) => {
      if (state.quantity > 1) {
        return {
          quantity: (state.quantity -= 1),
        };
      }
    });
  };

  render() {
    const products = this.props.products;
    const totalPrice = this.props.total;
    return (
      <>
        <ul>
          {products.map((product) => {
            return <Item product={product} />;
          })}
        </ul>
        <div className={styles.cart__invoice}>
          <div>
            Tax 21%:{" "}
            <span className={styles.tax}>
              ${(totalPrice * 0.21).toFixed(2)}
            </span>
          </div>
          <div>
            Quantity: <span className={styles.quantity}>{products.length}</span>
          </div>
          <div>
            Total:{" "}
            <span className={styles.total}> ${totalPrice.toFixed(2)} </span>
          </div>
          <Link to={routes.checkout}>
            <button className={styles.cart__invoice_order}>Order</button>
          </Link>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  products: getProducts(state),
  total: getTotalPrice(state),
});

export default connect(mapStateToProps, null)(CartProduct);
