import { gql } from "@apollo/client";

export const LOAD_ALL = gql`
  query {
    category {
      name: products {
        id
        name
        inStock
        gallery
        category
        prices {
          amount
          currency {
            label
            symbol
          }
        }
      }
    }
  }
`;

export const LOAD_CURRENCY = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;
