import React, { Component } from "react";
import Card from "../../components/Card/Card";
import "./All.scss";
import { LOAD_ALL } from "../../Graphql/Query";
import { graphql } from "@apollo/client/react/hoc";
import Skeleton from "../../components/Skeleton/Skeleton";

class All extends Component {
  displayCards() {
    const data = this.props.data;
    if (data.loading) {
      return [...new Array(6)].map((_, index) => <Skeleton key={index} />);
    } else {
      return data.category.name.map((item, index) => (
        <Card
          key={index}
          name={item.name}
          inStock={item.inStock}
          img={item.gallery[0]}
          symbol={item.prices[0].currency.symbol}
          price={item.prices[0].amount}
          onClick={() => console.log(item.id)}
        />
      ));
    }
  }

  render() {
    console.log(this.props);
    return (
      <>
        <div className="category__title">All</div>
        <div className="box__container">{this.displayCards()}</div>
      </>
    );
  }
}

export default graphql(LOAD_ALL)(All);
