import { Component } from "react";

import ProductsList from "../../components/ProductsList";
import Loader from "../../components/Loader";

import styles from "./Category.module.css";

class Category extends Component {
  constructor(props) {
    super(props);
    this.state ={
      currencies: this.props.currencies
    }
  }
  componentDidMount() {
    document.title = "Category | Nazar Kamaldinov test";
  }

  render() {
    const products = this.props.products;
    const categoryName = this.props.name;
    const loading = this.props.loading;
    const error = this.props.error;
    console.log(this.state.currencies);
    return (
      <main>
        <section className={styles.category}>
          {categoryName ? (
            <h1 className={styles.title}>{categoryName}</h1>
          ) : (
            <h1 className={styles.title}>{"all"}</h1>
          )}

          {products && (
            <ProductsList products={products} category={categoryName} />
          )}
        </section>

        {error && <p>{JSON.stringify(error.message)}</p>}

        {loading && <Loader />}
      </main>
    );
  }
}

export default Category;
