import React, { Component } from "react";
import "./FullCard.scss";
import { graphql } from "@apollo/client/react/hoc";
import { LOAD_PRODUCT } from "../../Graphql/Query";

class FullCard extends Component {

   displayInfo(){
    const data = this.props.data
    if(data.loading){
      return(<div>Loading...</div>)
    }else
    return(
      <div>
        <div className="fullCard__container">
          <img />
          <div className="fullCard__info">
            <div className="fullCard__name">{data.product.name}</div>
          </div>
        </div>
      </div>
    )
  }




  render() {
    console.log(this.props);
    console.log(window.location.pathname.replace("/item/", ""));
    return (
      <>
        {this.displayInfo()}
      </>
    );
  }
}

export default graphql(LOAD_PRODUCT)(FullCard);
