import React, { Component } from "react";
import "./Capacity.scss";

class Capacity extends Component {
  state = {
    capacitys: this.props.arr[0].items,
  };

  addClass = (e) => {
    this.setState({
      capacitys: this.state.capacitys.map((capasity) => ({
        ...capasity,
        isActive: e.target.id === capasity.id,
      })),
    });
  };
  render() {
    console.log(this.state.capacitys);
    return (
      <>
        <div className="capacity">
          <div className="capacity__title">CAPACITY:</div>
          <div className="capacity__container">
            {this.state.capacitys.map((item) => {
              return (
                <div
                  className={`capacity__box  ${
                    item.isActive ? " capacity__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.props.setCapacity(e.target.id)}
                  onClick={this.addClass}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Capacity;
