import React, { Component } from "react";
import "./Ports.scss";

class Ports extends Component {
  render() {
    return (
      <>
        <div className="adds">
          <div className="adds__title">WITH USB 3 PORTS:</div>
          <div className="adds__container">
            {this.props.arr[this.props.index].items.map((item) => {
              return (
                <div
                  className={`adds__box  ${
                    item.isActive ? " adds__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setPort(e.target.id)}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Ports;
