import React, { Component } from "react";
import Header from "./components/Header/header";
import { Routes, Route } from "react-router-dom";
import FullCard from "./Pages/FullCard/FullCard";
import Main from "./Pages/Main/Main";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencies: 0,
      productId: "",
      category: 0 || sessionStorage.getItem("Category"),
    };
    this.updateState = this.updateState.bind(this);
    this.getProductId = this.getProductId.bind(this);
    this.getCategory = this.getCategoryId.bind(this);
  }
  updateState(id) {
    this.setState({ currencies: id });
  }
  getProductId(id) {
    this.setState({ productId: id });
  }
  getCategoryId(id) {
    this.setState({ category: id });
  }

  render() {
    return (
      <div className="wrapper">
        <Header update={this.updateState} getCategoryId={this.getCategory} />
        <Routes>
          <Route
            path={"/all"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={"/clothes"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={"/tech"}
            element={
              <Main
                currencies={this.state.currencies}
                getProductId={this.getProductId}
                category={this.state.category}
              />
            }
          />
          <Route
            path={"/fullCard"}
            element={<FullCard productId={this.state.productId} />}
          />
        </Routes>
      </div>
    );
  }
}

export default App;
