import React from "react";
import { Component } from "react";
import "./Size.scss";

class Size extends Component {
  render() {
    console.log(this.props);
    return (
      <>
        <div className="size">
          <div className="size__title">SIZE:</div>
          <div className="size__container">
            {this.props.arr[this.props.index].items.map((item) => {
              return (
                <div
                  // className={`size__box  ${
                  //   this.state.active ? " size__box active" : ""
                  // }`}
                  className="size__box"
                  key={item.id}
                  id={item.id}
                  onClick={(e) => {
                    this.setSize(e.target.id);
                  }}
                >
                  <div>{item.value}</div>
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Size;
