import { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

import { getProducts, getTotalPrice } from "../../redux/cart/cart_selector";

import styles from "./CartProduct.module.css";
import routes from "../../routes";

class CartProduct extends Component {
  state = {
    quantity: 1,
  };

  handleAttributes = () => {
    //  alert('Selected');
  };

  increment = () => {
    this.setState((state) => {
      return {
        quantity: (state.quantity += 1),
      };
    });
  };

  decrement = () => {
    this.setState((state) => {
      if (state.quantity > 1) {
        return {
          quantity: (state.quantity -= 1),
        };
      }
    });
  };

  render() {
    const products = this.props.products;
    console.log(products);
    return (
      <>
        <ul>
          {products.map((product) => {
            return (
              <li className={styles.item} key={product.id}>
                <div className={styles.product}>
                  <p className={styles.product__name}>{product.name}</p>
                  <p className={styles.product__brand}>{product.brand}</p>
                  <p className={styles.product__price}>
                    ${product.prices[0].amount}
                  </p>

                  {product.attributes
                    ? product.attributes.map((attributes) => {
                        return (
                          <div key={attributes.id}>
                            <p className={styles.subtitle} key={attributes.id}>
                              {attributes.name}:
                            </p>

                            <div className={styles.attributes}>
                              {attributes.items.map((item) => {
                                return (
                                  <button
                                    type="button"
                                    className={styles.radio}
                                    key={item.id}
                                    onClick={this.handleAttributes}
                                    style={{ backgroundColor: `${item.value}` }}
                                    title={item.displayValue}
                                  >
                                    {attributes.id === "Color"
                                      ? ""
                                      : item.displayValue}
                                  </button>
                                );
                              })}
                            </div>
                          </div>
                        );
                      })
                    : null}
                </div>

                <div className={styles.wrapper}>
                  <div className={styles.counters}>
                    <button
                      type="button"
                      className={styles.counters__button}
                      onClick={this.increment}
                    >
                      +
                    </button>
                    <span className={styles.counters__value}>
                      {this.state.quantity}
                    </span>
                    <button
                      type="button"
                      className={styles.counters__button}
                      onClick={this.decrement}
                    >
                      -
                    </button>
                  </div>

                  <div className={styles.gallery}>
                    <img
                      src={product.gallery[0]}
                      className={styles.gallery__image}
                      alt={product.name}
                      title={product.name}
                      width="141"
                      height="185"
                    />
                  </div>
                </div>
              </li>
            );
          })}
        </ul>
        <div className={styles.cart__invoice}>
          <div>Tax 21%:</div>
          <div>
            Quantity:<span>{products.length}</span>
          </div>
          <div>
            Total:<span></span>
          </div>
          <Link to={routes.checkout}>
            <button className={styles.cart__invoice_order}>Order</button>
          </Link>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  products: getProducts(state),
  total: getTotalPrice(state),
});

export default connect(mapStateToProps, null)(CartProduct);
