import React, { Component } from "react";
import "./header.scss";
import logo from "./assets/a-logo.png";
import { Link } from "react-router-dom";

class Header extends Component {
  state = {
    menuItems: [
      { value: "ALL", link: "/all", id: 0 },
      { value: "CLOTHES", link: "/clothes", id: 1 },
      { value: "TECH", link: "/tech", id: 2 },
    ],
    moneyChange: [
      { value: "$ USD", link: "/" },
      { value: "€ EUR", link: "/" },
      { value: "¥ JPY", link: "/" },
    ],
  };

  addClass = (e) => {
    this.setState({
      menuItem: this.state.menuItems.map((menu) => ({
        ...menu,
        isActive: e.target.id === menu.id,
      })),
    });
  };

  render() {
    return (
      <>
        <div className="header__container">
          <div className="header__menu">
            {this.state.menuItems.map((item) => (
              <Link
                to={item.link}
                className={`header__menu_item ${
                  item.isActive ? " header__menu_item active" : ""
                }`}
                key={item.id}
                id={item.id}
                onClick={this.addClass}
              >
                {item.value}
              </Link>
            ))}
          </div>
          <div className="header__logo">
            <Link to={"/all"}>
              <img src={logo} alt="logo" />
            </Link>
          </div>
          <div className="header__assets">
            <div className="header__assets_money">
              <div className="dropdown">
                <i className="fa-solid  fa-xl fa-dollar-sign"></i>
                <div className="dropdown-content">
                  {this.state.moneyChange.map((item, index) => (
                    <Link to={item.link} key={index}>
                      {item.value}{" "}
                    </Link>
                  ))}
                </div>
                <svg
                  width="8"
                  height="4"
                  viewBox="0 0 8 4"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="dropdown-arrow"
                >
                  <path d="M1 0.5L4 3.5L7 0.5" stroke="black" />
                </svg>
              </div>
            </div>
            <div className="header__assets_cart">
              <i className="fas fa-regular fa-xl fa-cart-shopping"></i>
              <div className="header__assets_cart-count">10</div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Header;
