import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    products: [],
  },
  reducers: {
    addProduct(state, { payload }) {
      state.products.push(payload);
    },
    deleteItem(state, { payload }) {
      state.products = state.products.filter((item) => item.id !== payload);
    },
  },
});

