import { Link } from "react-router-dom";
import React, { Component } from "react";
import { LOAD_CURRENCY } from "../../Graphql/Query";
import { graphql } from "@apollo/client/react/hoc";
import "./Dropdown.scss"

class DropDown extends Component {
  state = {
    moneyChange: [
      { value: "$ USD", link: "/" },
      { value: "€ EUR", link: "/" },
      { value: "¥ JPY", link: "/" },
    ],
  };

  render() {
    return (
      <div className="dropdown">
        <i className="fa-solid  fa-xl fa-dollar-sign"></i>
        <div className="dropdown-content">
          {this.state.moneyChange.map((item, index) => (
            <Link to={item.link} key={index}>
              {item.value}{" "}
            </Link>
          ))}
        </div>
        <svg
          width="8"
          height="4"
          viewBox="0 0 8 4"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          className="dropdown-arrow"
        >
          <path d="M1 0.5L4 3.5L7 0.5" stroke="black" />
        </svg>
      </div>
    );
  }
}

export default graphql(LOAD_CURRENCY)(DropDown);
