import { Component } from 'react';
import { graphql } from '@apollo/client/react/hoc';
import {LOAD_ALL} from "./GraphQl";
import { Suspense, lazy } from 'react';
import { Switch, Route } from 'react-router-dom';

import Container from './components/Container';
import AppBar from './components/AppBar';
import Loader from './components/Loader';

import './App.css';


const Category = lazy(() =>
  import('./pages/Category'),
);

const Product = lazy(() =>
  import('./pages/Product'),
);

const Cart = lazy(() =>
  import('./pages/Cart'),
);

const Checkout = lazy(() =>
  import('./pages/Checkout'),
);

const NoFound = lazy(()=>
    import('./pages/NoFound'),
)

class App extends Component {
  render() {
    const { data } = this.props;
    const { error, loading } = data;
console.log(1)
    const all = data?.categories?.reduce((acc, item) => {
      acc.push(...item.products);
      return acc;
    }, []);

    return (
      <Container>
        <AppBar categories={data.categories}/>

        <Suspense fallback={<Loader />}>
          <Switch>
            {data.categories && (
              <Route exact path="/">
                <Category
                  products={all}
                  error={data.error}
                  loading={data.loading}
                />
              </Route>
            )}

            {data.categories &&
              data.categories.map(category => {
                return (
                  <Route exact path={`/${category.name}`} key={category.name}>
                    <Category
                      products={category.products}
                      name={category.name}
                      error={data.error}
                      loading={data.loading}
                    />
                  </Route>
                );
              })}

            {data.categories &&
              data.categories.map(category => {
                return (
                  <Route
                    exact
                    path={`/${category.name}/:productId`}
                    key={category.name}
                  >
                    <Product />
                  </Route>
                );
              })}

            <Route exact path="/cart">
              <Cart />
            </Route>

            <Route exact path="/checkout">
              <Checkout />
            </Route>

            <Route path="*" element={<NoFound />} />
          </Switch>
        </Suspense>

        {error && <p>{JSON.stringify(error.message)}</p>}

        {loading && <Loader />}

      </Container>
    );
  }
}

export default graphql(LOAD_ALL)(App);
