import React, { Component } from "react";
import "./header.scss";
import logo from "./assets/a-logo.png";
import { NavLink, Link } from "react-router-dom";
import DropDown from "../DropDown/DropDown";
import Overlay from "../OverlayCart/Overlay";

class Header extends Component {
  state = {
    menuItems: [
      { value: "ALL", link: "/", id: 0 },
      { value: "CLOTHES", link: "/clothes", id: 1 },
      { value: "TECH", link: "/tech", id: 2 },
    ],
  };

  addClass = (e) => {
    this.setState({
      menuItem: this.state.menuItems.map((menu) => ({
        ...menu,
        isActive: e.target.id === menu.id,
      })),
    });
  };

  Overlay() {
    return <Overlay items={this.props.items} />;
  }

  render() {
    console.log(this.props.items);
    return (
      <>
        <div className="header__container">
          <div className="header__menu">
            {this.state.menuItems.map((item) => (
              <NavLink
                to={item.link}
                className={`header__menu_item  ${
                  item.isActive ? " header__menu_item active" : ""
                }`}
                key={item.id}
                id={item.id}
                onClick={this.addClass}
                onClick={() => {
                  this.props.getCategoryId(item.id);
                  sessionStorage.setItem("Category", item.id);
                }}
              >
                {item.value}
              </NavLink>
            ))}
          </div>
          <div className="header__logo">
            <Link to={"/"}>
              <img
                src={logo}
                alt="logo"
                onClick={() => {
                  this.props.getCategoryId(0);
                }}
              />
            </Link>
          </div>
          <div className="header__assets">
            <div className="header__assets_money">
              <DropDown update={this.props.update} />
            </div>
            <div className="header__assets_cart">
              <i
                className="fas fa-regular fa-xl fa-cart-shopping"
                onClick={this.Overlay}
              ></i>
              <div className="header__assets_cart-count">10</div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Header;
