import React, { Component } from "react";
import { LOAD_CURRENCY } from "../../Graphql/Query";
import { graphql } from "@apollo/client/react/hoc";
import "./Dropdown.scss";

class DropDown extends Component {
  displayDropDown() {
    const data = this.props.data;
    if (data.loading) {
      return <div>Loading...</div>;
    } else {
      return data.currencies.map((item, index) => (
        <ul key={index} id={item.label}>
          <li onClick={(event) => console.log(event.target)}>
            {item.symbol} {item.label}
          </li>
        </ul>
      ));
    }
  }

  render() {
    return (
      <div className="dropdown">
        <i className="fa-solid  fa-xl fa-dollar-sign"></i>
        <div className="dropdown-content">{this.displayDropDown()}</div>
        <svg
          width="8"
          height="4"
          viewBox="0 0 8 4"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          className="dropdown-arrow"
        >
          <path d="M1 0.5L4 3.5L7 0.5" stroke="black" />
        </svg>
      </div>
    );
  }
}

export default graphql(LOAD_CURRENCY)(DropDown);
