import React, { Component } from "react";
import "./CartItem.scss";

class CartItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photoId: 0,
      countOfItem: 1,
    };
    this.setNextPhoto = this.setNextPhoto.bind(this);
    this.setPrevPhoto = this.setPrevPhoto.bind(this);
    this.incCount = this.incCount.bind(this);
    this.decCount = this.decCount.bind(this);
  }
  setNextPhoto() {
    this.setState({
      photoId: this.state.photoId + 1,
    });
    if (this.state.photoId >= this.props.data.gallery.length - 1) {
      this.setState({
        photoId: 0,
      });
    }
  }
  setPrevPhoto() {
    this.setState({ photoId: this.state.photoId - 1 });
    if (this.state.photoId <= 0) {
      this.setState({
        photoId: 0,
      });
    }
  }
  incCount() {
    this.setState({ countOfItem: this.state.countOfItem + 1 });
  }

  decCount() {
    this.setState({ countOfItem: this.state.countOfItem - 1 });
    if (this.state.countOfItem >= 0) {
      if (
        window.confirm(`Do you wont delete ${this.props.data.name} from cart? `)
      ) {
        console.log(1);
      } else {
        this.setState({ countOfItem: 1 });
      }
    }
  }
  render() {
    return (
      <>
        <div className="cartItem__line"></div>
        <div className="cartItem__container">
          <div className="cartItem__info">
            <div className="cartItem__name">{this.props.data.name}</div>
            <div className="cartItem__brand">{this.props.data.brand}</div>
            <div className="cartItem__price">
              {" "}
              {this.props.data.prices[this.props.currencies].currency.symbol}
              {this.props.data.prices[this.props.currencies].amount}
            </div>
          </div>
          <div className="cartItem__adds">
            <div className="cardItem__count">
              <div className="cartItem__add" onClick={this.incCount}>
                <span>+</span>
              </div>
              <div className="cartItem__count_numb">
                {this.state.countOfItem}
              </div>
              <div className="cartItem__minus" onClick={this.decCount}>
                -
              </div>
            </div>
            <div className="cartItem__carousel">
              <div className="carousel__prev" onClick={this.setPrevPhoto}>
                <svg
                  width="8"
                  height="14"
                  viewBox="0 0 8 14"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="prev"
                >
                  <path
                    d="M7.25 1.06857L1.625 6.6876L7.25 12.3066"
                    stroke="white"
                  />
                </svg>
              </div>
              <div className="carousel__next" onClick={this.setNextPhoto}>
                <svg
                  width="8"
                  height="14"
                  viewBox="0 0 8 14"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="next"
                >
                  <path
                    d="M0.75 1.06808L6.375 6.68711L0.75 12.3062"
                    stroke="white"
                  />
                </svg>
              </div>
              <img
                className="cartItem__photo"
                src={this.props.data.gallery[this.state.photoId]}
                alt="small_pic"
              />
              ))}
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default CartItem;
