import React, { Component } from "react";
import "./Color.scss";

class Color extends Component {
  render() {
    return (
      <>
        <div className="color">
          <div className="color__title">COLOR:</div>
          <div className="color__container">
            {this.props.arr[this.props.index].items.map((item) => {
              return (
                <div
                  className={`color__box  ${
                    item.isActive ? " color__box active" : ""
                  }`}
                  key={item.id}
                  style={{ background: `${item.value}` }}
                  id={item.id}
                  onClick={(e) => this.setColor(e.target.id)}
                ></div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Color;
