import React, { Component } from "react";
import "./FullCard.scss";
import { Query } from "@apollo/client/react/components";
import { gql } from "@apollo/client";
import parse from "html-react-parser";

class FullCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photoId: 0,
      activeState: [
        { sizeId: "" },
        { colorId: "" },
        { capacityId: "" },
        { portId: "" },
        { touchId: "" },
      ],
    };
    this.setPhoto = this.setPhoto.bind(this);
    this.setSize = this.setSize.bind(this);
    this.setColor = this.setColor.bind(this);
    this.setCapacity = this.setCapacity.bind(this);
    this.setPort = this.setPort.bind(this);
    this.setTouch = this.setTouch.bind(this);
  }
  setSize(id) {
    this.setState({ sizeId: id });
  }
  setColor(id) {
    this.setState({ colorId: id });
  }
  setCapacity(id) {
    this.setState({ capacityId: id });
  }
  setPort(id) {
    this.setState({ portId: id });
  }
  setTouch(id) {
    this.setState({ touchId: id });
  }
  setPhoto(id) {
    this.setState({ photoId: id });
  }

  // addClass = (e) => {
  //   this.setState({
  //      : this.state.menuItems.map((menu) => ({
  //       ...menu,
  //       isActive: e.target.id === menu.id,
  //     })),
  //   });
  // };

  isStock(inStock) {
    if (inStock) {
      return <button className="fullCard__button-add">ADD TO CART</button>;
    } else {
      return <div className="fullCard__stock">OUT OF STOCK</div>;
    }
  }
  isSize(arr) {
    if (arr.find((obj) => obj.name === "Size")) {
      let index = arr.findIndex((elem) => elem.name === "Size");
      return (
        <div className="size">
          <div className="size__title">SIZE:</div>
          <div className="size__container">
            {arr[index].items.map((item) => {
              return (
                <div
                  className={`size__box  ${
                    item.isActive ? " size__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setSize(e.target.id)}
                >
                  <div>{item.value}</div>
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  }
  isColor(arr) {
    if (arr.find((obj) => obj.name === "Color")) {
      let index = arr.findIndex((elem) => elem.name === "Color");
      return (
        <div className="color">
          <div className="color__title">COLOR:</div>
          <div className="color__container">
            {arr[index].items.map((item) => {
              return (
                <div
                  className={`color__box  ${
                    item.isActive ? " color__box active" : ""
                  }`}
                  key={item.id}
                  style={{ background: `${item.value}` }}
                  id={item.id}
                  onClick={(e) => this.setColor(e.target.id)}
                ></div>
              );
            })}
          </div>
        </div>
      );
    }
  }
  isCapacity(arr) {
    if (arr.find((obj) => obj.name === "Capacity")) {
      let index = arr.findIndex((elem) => elem.name === "Capacity");
      return (
        <div className="capacity">
          <div className="capacity__title">CAPACITY:</div>
          <div className="capacity__container">
            {arr[index].items.map((item) => {
              return (
                <div
                  className={`capacity__box  ${
                    item.isActive ? " capacity__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setCapacity(e.target.id)}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  }
  isPort(arr) {
    if (arr.find((obj) => obj.id === "With USB 3 ports")) {
      let index = arr.findIndex((elem) => elem.name === "With USB 3 ports");
      return (
        <div className="adds">
          <div className="adds__title">WITH USB 3 PORTS:</div>
          <div className="adds__container">
            {arr[index].items.map((item) => {
              return (
                <div
                  className={`adds__box  ${
                    item.isActive ? " adds__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setPort(e.target.id)}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  }
  isTouchId(arr) {
    if (arr.find((obj) => obj.id === "Touch ID in keyboard")) {
      let index = arr.findIndex((elem) => elem.name === "Touch ID in keyboard");
      return (
        <div className="adds">
          <div className="adds__title">TOUCH ID IN KEYBOARD:</div>
          <div className="adds__container">
            {arr[index].items.map((item) => {
              return (
                <div
                  className={`adds__box  ${
                    item.isActive ? " adds__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setTouch(e.target.id)}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  }

  render() {
    console.log(this.state);
    return (
      <>
        <Query
          query={gql` 
  query {
    product(id: "${
      window.location.pathname.replace("/item/", "") || this.props.productId
    }") {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        amount
        currency {
          label
          symbol
        }
      }
      brand
    }
  }
`}
        >
          {({ loading, data }) => {
            if (loading) {
              return <div>Loading...</div>;
            } else {
              const attributes = data.product.attributes;
              return (
                <div>
                  <div className="fullCard__container">
                    <div className="fullCard__smallImages">
                      {data.product.gallery.map((item, index) => (
                        <img
                          className="fullCard__smallImagesIco"
                          src={item}
                          alt="small-icon"
                          id={index}
                          onClick={(e) => this.setPhoto(e.target.id)}
                        />
                      ))}
                    </div>
                    <div className="fullCard__mainImage">
                      <img
                        className="fullCard__mainImage"
                        src={data.product.gallery[this.state.photoId]}
                        alt={"item"}
                      />
                    </div>
                    <div className="fullCard__info">
                      <div className="fullCard__name">{data.product.name}</div>
                      <div className="fullCard__brand">
                        {data.product.brand}
                      </div>
                      {this.isSize(attributes)}
                      {this.isColor(attributes)}
                      {this.isCapacity(attributes)}
                      {this.isPort(attributes)}
                      {this.isTouchId(attributes)}
                      <div className="fullCard__Price">
                        <div className="fullCard__price_title">PRICE:</div>
                        <div className="fullCard__price">
                          {
                            data.product.prices[this.props.currencies].currency
                              .symbol
                          }
                          {data.product.prices[this.props.currencies].amount}
                        </div>
                      </div>
                      {this.isStock(data.product.inStock)}
                      <div className="fullCard__description">
                        {parse(data.product.description)}
                      </div>
                    </div>
                  </div>
                </div>
              );
            }
          }}
        </Query>
      </>
    );
  }
}

export default FullCard;
