import React, { Component } from "react";
import "./FullCard.scss";
import { Query } from "@apollo/client/react/components";
import { gql } from "@apollo/client";

class FullCard1 extends Component {
  render() {
    console.log(this.props);
    return (
      <>
        <Query
          query={gql` 
  query {
    product(id: "${window.location.pathname.replace("/item/", "")}") {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        amount
        currency {
          label
          symbol
        }
      }
      brand
    }
  }
`}
          children={(result) => this.props}
        >
          <div>
            <div className="fullCard__container">
              <img />
              <div className="fullCard__info">
                <div className="fullCard__name">123</div>
              </div>
            </div>
          </div>
        </Query>
      </>
    );
  }
}

export default FullCard1;
