import { combineReducers } from "redux";
import { createReducer, createSlice, current } from "@reduxjs/toolkit";

import {
  addProductRequest,
  addProductSuccess,
  addProductError,
  deleteProductSuccess,
  deleteProductRequest,
  deleteProductError,
} from "./cart_action";
//
// const initial = {
//   products: [],
// };

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    products: [],
  },
  reducers: {
    addProduct(state, { payload }) {
      state.products.push(payload);
    },
  },
});

// const products = createReducer(initial.products, {
//   [addProductSuccess]: (state, { payload }) => [...state, payload],
// });
//
// const deleteItem = createReducer(, {
//   [deleteProductSuccess]: (state, { payload }) => {
//     console.log(current(state));
//     // return (state.products = state.products.filter(
//     //   (item) => item.id !== payload
//     // ));
//   },
// });
//
export const error = createReducer(false, {
  [addProductRequest]: () => false,
  [addProductSuccess]: () => false,
  [addProductError]: () => true,
  [deleteProductRequest]: () => false,
  [deleteProductSuccess]: () => false,
  [deleteProductError]: () => true,
});

export const isLoading = createReducer(false, {
  [addProductRequest]: () => true,
  [addProductSuccess]: () => false,
  [addProductError]: () => false,
  [deleteProductRequest]: () => true,
  [deleteProductSuccess]: () => false,
  [deleteProductError]: () => false,
});

export const { addProduct } = cartSlice.actions;
// export default combineReducers({
//   deleteItem,
//   Products,
//   error,
//   isLoading,
// });

export default cartSlice.reducer;
