import React, { Component } from "react";
import "./CartItem.scss";

class CartItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photoId: 0,
      length: 0,
    };
    this.setNextPhoto = this.setNextPhoto.bind(this);
    this.setPrevPhoto = this.setPrevPhoto.bind(this);
  }
  setNextPhoto(length) {
    this.setState({ photoId: this.state.photoId + 1 < length });
  }
  setPrevPhoto() {
    this.setState({ photoId: this.state.photoId - 1 > -1 });
  }
  render() {
    //const photoArrLength = this.props.data.gallery.length;
    return (
      <>
        <div className="cartItem__line"></div>
        <div className="cartItem__container">
          <div className="cartItem__info">
            <div className="cartItem__name">{this.props.data.name}</div>
            <div className="cartItem__brand">{this.props.data.brand}</div>
            <div className="cartItem__price">
              {" "}
              {this.props.data.prices[this.props.currencies].currency.symbol}
              {this.props.data.prices[this.props.currencies].amount}
            </div>
          </div>
          <div className="cartItem__adds">
            <div className="cardItem__count">
              <div className="cartItem__add">
                <span>+</span>
              </div>
              <div className="cartItem__count_numb">1</div>
              <div className="cartItem__minus">-</div>
            </div>
            <div className="cartItem__carousel">
              <div className="carousel__prev" onClick={this.setPrevPhoto()}>
                <svg
                  width="8"
                  height="14"
                  viewBox="0 0 8 14"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="prev"
                >
                  <path
                    d="M7.25 1.06857L1.625 6.6876L7.25 12.3066"
                    stroke="white"
                  />
                </svg>
              </div>
              <div
                className="carousel__next"
                onClick={() => this.setNextPhoto(photoArrLength)}
              >
                <svg
                  width="8"
                  height="14"
                  viewBox="0 0 8 14"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="next"
                >
                  <path
                    d="M0.75 1.06808L6.375 6.68711L0.75 12.3062"
                    stroke="white"
                  />
                </svg>
              </div>
              <img
                className="cartItem__photo"
                src={this.props.data.gallery[this.state.photoId]}
                alt="small_pic"
              />
              ))}
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default CartItem;
