import React, { Component } from "react";
import "./Capacity.scss";

class Capacity extends Component {
  render() {
    return (
      <>
        <div className="capacity">
          <div className="capacity__title">CAPACITY:</div>
          <div className="capacity__container">
            {this.props.arr[this.props.index].items.map((item) => {
              return (
                <div
                  className={`capacity__box  ${
                    item.isActive ? " capacity__box active" : ""
                  }`}
                  key={item.id}
                  id={item.id}
                  onClick={(e) => this.setCapacity(e.target.id)}
                >
                  {item.value}
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Capacity;
