import { gql } from "@apollo/client";

export const LOAD_ALL = gql`
  query {
    categories {
      name
      products {
        id
        name
        inStock
        gallery
        category
        prices {
          amount
          currency {
            label
            symbol
          }
        }
      }
    }
  }
`;

export const LOAD_CURRENCY = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

export const LOAD_PRODUCT = () => {
  gql`
  query {
    product(id: "${window.location.pathname.replace("/item/", "")}") {
      id
      name
      inStock
      gallery
      description
      category
      attributes {
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        amount
        currency {
          label
          symbol
        }
      }
      brand
    }
  }
`;
};
