import React, { Component } from "react";
import "./header.scss";
import logo from "./assets/a-logo.png";
import { NavLink, Link } from "react-router-dom";

class Header extends Component {
  state = {
    menuItems: [
      { value: "WOMEN", link: "/women", id: 0 },
      { value: "MEN", link: "/men", id: 1 },
      { value: "KIDS", link: "/kids", id: 2 },
    ],
  };
  addClass = (e) => {
    this.setState({
      menuItem: this.state.menuItems.map((menu) => ({
        ...menu,
        isActive: e.target.id === menu.id,
      })),
    });
  };

  render() {
    return (
      <>
        <div className="header__container">
          <div className="header__menu">
            {this.state.menuItems.map((item, index) => (
              <NavLink
                to={item.link}
                // style={{ color: item.isActive ? "green" : "black" }}
                className={`header__menu_item ${
                  item.isActive ? " header__menu_item active" : ""
                }`}
                key={index}
                id={item.id}
                onClick={this.addClass}
              >
                {item.value}
              </NavLink>
            ))}
          </div>
          <div className="header__logo">
            <Link to={"/"}>
              <img src={logo} alt="logo" />
            </Link>
          </div>
          <div className="header__assets">
            <div className="header__assets_money">
              <div className="dropdown">
                <i className="fa-solid  fa-xl fa-dollar-sign"></i>
                <div className="dropdown-content">
                  <Link to="/">$ USD</Link>
                  <Link to="/">€ EUR</Link>
                  <Link to="/">¥ JPY</Link>
                </div>
                <svg
                  width="8"
                  height="4"
                  viewBox="0 0 8 4"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  className="dropdown-arrow"
                >
                  <path d="M1 0.5L4 3.5L7 0.5" stroke="black" />
                </svg>
              </div>
            </div>
            <div className="header__assets_cart">
              <i className="fas fa-regular fa-xl fa-cart-shopping"></i>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Header;
