import React, { Component } from "react";
import "./Color.scss";

class Color extends Component {
  state = {
    colors: this.props.arr.items,
  };

  addClass = (e) => {
    this.setState({
      colors: this.state.colors.map((color) => ({
        ...color,
        isActive: e.target.id === color.id,
      })),
    });
  };
  render() {
    return (
      <>
        <div className="color">
          <div className="color__title">COLOR:</div>
          <div className="color__container">
            {this.state.colors.map((item) => {
              return (
                <div
                  className={`color__box  ${item.isActive ? "  active" : ""}`}
                  key={item.id}
                  style={{ background: `${item.value}` }}
                  id={item.id}
                  onClick={(e) => this.props.setColor(e.target.id)}
                  onClick={this.addClass}
                ></div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Color;
