import React, { Component } from "react";
import Card from "../../components/Card/Card";
import "./Main.scss";
import { LOAD_ALL } from "../../Graphql/Query";
import { graphql } from "@apollo/client/react/hoc";
import Skeleton from "../../components/Skeleton/Skeleton";

class Main extends Component {
  capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  displayCards() {
    const data = this.props.data;
    if (data.loading) {
      return [...new Array(6)].map((_, index) => <Skeleton key={index} />);
    } else {
      return data.categories[this.props.category].products.map(
        (item, index) => (
          <Card
            key={index}
            id={item.id}
            name={item.name}
            inStock={item.inStock}
            img={item.gallery[0]}
            symbol={item.prices[this.props.currencies].currency.symbol}
            price={item.prices[this.props.currencies].amount}
          />
        )
      );
    }
  }

  render() {
    return (
      <>
        <div className="category__title">
          {/*{this.capitalizeFirstLetter(*/}
          {/*  this.props.data.categories[this.props.category].name*/}
          {/*)}*/}
        </div>
        <div className="box__container">{this.displayCards()}</div>
      </>
    );
  }
}

export default graphql(LOAD_ALL)(Main);
