import React, {Component} from 'react';
import "./header.scss"
import logo from "./assets/a-logo.png"
import {Link} from "react-router-dom";


class Header extends Component{



    render () {
        const menuItems = [{value:"WOMEN", link:"/women",id:0},{value:"MEN", link:"/men" ,id:1},{value:"KIDS", link:"/kids",id:2}]
        return(
            <>
        <div className="header__container">
            <div className="header__menu">
                    {menuItems.map((item, index) => (
                            <Link  to={item.link} className="header__menu_item" key={index} id={item.id}>{item.value}</Link>
                    ))}
            </div>
            <div className="header__logo"><Link to={"/"}><img src={logo} alt="logo"/></Link></div>
            <div className="header__assets">
            <div className="header__assets_money" >
                <i className="fa-solid  fa-xl fa-dollar-sign"></i>
            </div>
            <div className="header__assets_cart">
                <i className="fas fa-regular fa-xl fa-cart-shopping"></i>
            </div>
            </div>
            </div>

       </>
        )
    }
}

export default Header
