import { gql } from "@apollo/client";
import { productId } from "../App";

export const LOAD_ALL = gql`
  query {
    categories {
      name
      products {
        id
        name
        inStock
        gallery
        category
        prices {
          amount
          currency {
            label
            symbol
          }
        }
      }
    }
  }
`;

export const LOAD_CURRENCY = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

