import React, { Component } from "react";
import "./Cart.scss";
import CartItem from "../../components/CartItem/CartItem";

class Cart extends Component {
  displayProduct() {
    const data = this.props.data;
    return data.map((item, index) => (
      <CartItem
        data={item}
        currencies={this.props.currencies}
        deleteItem={this.deleteItem()}
        key={index}
      />
    ));
  }
  deleteItem(id) {
    return this.props.data.filter((item) => item.name !== id);
  }
  render() {
    return (
      <>
        <div className="cart__title">CART</div>
        {this.displayProduct()}
        <div className="cart__line"></div>
        <div className="cart__invoice">
          <div className="cart__invoice_tax">Tax 21%:</div>
          <div className="cart__invoice_quantity">Quantity:</div>
          <div className="cart__invoice_total">Total:</div>
          <button className="cart__invoice_order">Order</button>
        </div>
      </>
    );
  }
}

export default Cart;
