import React, { Component } from "react";
import Header from "./components/Header/header";
import { Routes, Route } from "react-router-dom";
import FullCard from "./Pages/FullCard/FullCard";
import All from "./Pages/All/All";
import "./App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currencies: 0,
      productId: "",
      category:"All"
    };
    this.updateState = this.updateState.bind(this);
    this.getProductId = this.getProductId.bind(this);
    this.getCategory = this.getCategory.bind(this)
  }
  updateState(id) {
    this.setState({ currencies: id });
  }
  getProductId(id) {
    this.setState({ productId: id });
  }
  getCategory(name){
    this.setState({category: name})
  }

  render() {
    return (
      <div className="wrapper">
        <Header update={this.updateState}  getCategory={this.getCategory}/>
        <Routes>
          <Route
            path={"/all"}
            element={
              <All
                currencies={this.state.currencies}
                getProductId={this.getProductId}
              />
            }
          />
          <Route
            path={"/fullCard"}
            element={<FullCard productId={this.state.productId} />}
          />
        </Routes>
      </div>
    );
  }
}

export default App;
