import React, { Component } from "react";
import "./Cart.scss";
import CartItem from "../../components/CartItem/CartItem";

class Cart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      countOfItem: 1,
    };

    this.incCount = this.incCount.bind(this);
    this.decCount = this.decCount.bind(this);
  }

  displayProduct() {
    const data = this.props.data;
    return data.map((item, index) => (
      <CartItem
        data={item}
        currencies={this.props.currencies}
        incCount={this.incCount}
        decCount={this.decCount}
        key={index}
      />
    ));
  }

  incCount() {
    this.setState({ countOfItem: this.state.countOfItem + 1 });
  }

  decCount() {
    this.setState({ countOfItem: this.state.countOfItem - 1 });
    if (this.state.countOfItem <= 1) {
      if (
        window.confirm(`Do you wont delete ${this.props.data.name} from cart? `)
      ) {
        console.log(1);
      } else {
        this.setState({ countOfItem: 1 });
      }
    }
  }

  render() {
    console.log(this.props.data);
    return (
      <>
        <div className="cart__title">CART</div>
        {this.displayProduct()}
        <div className="cart__line"></div>
        <div className="cart__invoice">
          <div className="cart__invoice_tax">Tax 21%:</div>
          <div className="cart__invoice_quantity">Quantity:</div>
          <div className="cart__invoice_total">Total:</div>
          <button className="cart__invoice_order">Order</button>
        </div>
      </>
    );
  }
}

export default Cart;
