import React, { Component } from "react";
import "./CartItem.scss";
import Size from "../AttributesComponents/Size/Size";
import Color from "../AttributesComponents/Color/Color";
import Capacity from "../AttributesComponents/Capacity/Capacity";
import Ports from "../AttributesComponents/Ports/Ports";
import TouchId from "../AttributesComponents/TouchId/TouchId";

class CartItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photoId: 0,
      countOfItem: 1,
    };
    this.setNextPhoto = this.setNextPhoto.bind(this);
    this.setPrevPhoto = this.setPrevPhoto.bind(this);
    this.incCount = this.incCount.bind(this);
    this.decCount = this.decCount.bind(this);
  }
  setNextPhoto() {
    this.setState({
      photoId: this.state.photoId + 1,
    });
    if (this.state.photoId >= this.props.data.gallery.length - 1) {
      this.setState({
        photoId: 0,
      });
    }
  }
  setPrevPhoto() {
    this.setState({ photoId: this.state.photoId - 1 });
    if (this.state.photoId <= 0) {
      this.setState({
        photoId: 0,
      });
    }
  }
  isCarousel() {
    if (this.props.data.gallery.length > 1) {
      return (
        <>
          <div className="carousel__prev" onClick={this.setPrevPhoto}>
            <svg
              width="8"
              height="14"
              viewBox="0 0 8 14"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              className="prev"
            >
              <path
                d="M7.25 1.06857L1.625 6.6876L7.25 12.3066"
                stroke="white"
              />
            </svg>
          </div>
          <div className="carousel__next" onClick={this.setNextPhoto}>
            <svg
              width="8"
              height="14"
              viewBox="0 0 8 14"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              className="next"
            >
              <path
                d="M0.75 1.06808L6.375 6.68711L0.75 12.3062"
                stroke="white"
              />
            </svg>
          </div>
        </>
      );
    }
  }

  incCount() {
    this.setState({ countOfItem: this.state.countOfItem + 1 });
  }

  decCount() {
    this.setState({ countOfItem: this.state.countOfItem - 1 });
    if (this.state.countOfItem <= 1) {
      if (
        window.confirm(`Do you wont delete ${this.props.data.name} from cart? `)
      ) {
        this.props.deleteItem(this.props.data.name);
      } else {
        this.setState({ countOfItem: 1 });
      }
    }
  }
  isSize(arr) {
    if (arr.find((obj) => obj.name === "Size")) {
      let index = arr.findIndex((elem) => elem.name === "Size");
      return <Size arr={arr[index]} setSize={this.setSize} />;
    }
  }
  isColor(arr) {
    if (arr.find((obj) => obj.name === "Color")) {
      let index = arr.findIndex((elem) => elem.name === "Color");
      return <Color arr={arr[index]} setColor={this.setColor} />;
    }
  }
  isCapacity(arr) {
    if (arr.find((obj) => obj.name === "Capacity")) {
      let index = arr.findIndex((elem) => elem.name === "Capacity");
      return <Capacity arr={arr[index]} setCapacity={this.setCapacity} />;
    }
  }
  isPort(arr) {
    if (arr.find((obj) => obj.id === "With USB 3 ports")) {
      let index = arr.findIndex((elem) => elem.name === "With USB 3 ports");
      return <Ports arr={arr[index]} setPort={this.setPort} />;
    }
  }
  isTouchId(arr) {
    if (arr.find((obj) => obj.id === "Touch ID in keyboard")) {
      let index = arr.findIndex((elem) => elem.name === "Touch ID in keyboard");
      return <TouchId arr={arr[index]} setTouch={this.setTouch} />;
    }
  }

  render() {
    const attributes = this.props.data.attributes;
    return (
      <>
        <div className="cartItem__line"></div>
        <div className="cartItem__container">
          <div className="cartItem__info">
            <div className="cartItem__name">{this.props.data.name}</div>
            <div className="cartItem__brand">{this.props.data.brand}</div>
            <div className="cartItem__price">
              {" "}
              {this.props.data.prices[this.props.currencies].currency.symbol}
              {this.props.data.prices[this.props.currencies].amount}
            </div>
            {this.isSize(attributes)}
            {this.isColor(attributes)}
            {this.isCapacity(attributes)}
            {this.isPort(attributes)}
            {this.isTouchId(attributes)}
          </div>
          <div className="cartItem__adds">
            <div className="cardItem__count">
              <div className="cartItem__add" onClick={this.incCount}>
                <span>+</span>
              </div>
              <div className="cartItem__count_numb">
                {this.state.countOfItem}
              </div>
              <div className="cartItem__minus" onClick={this.decCount}>
                -
              </div>
            </div>
            <div className="cartItem__carousel">
              {this.isCarousel}
              <img
                className="cartItem__photo"
                src={this.props.data.gallery[this.state.photoId]}
                alt="small_pic"
              />
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default CartItem;
