import React from "react";
import { Component } from "react";
import "./Size.scss";

class Size extends Component {
  state = {
    sizes: [this.props.arr],
  };

  addClass = (e) => {
    this.setState({
      sizes: this.state.sizes.map((size) => ({
        ...size,
        isActive: e.target.id === size.id,
      })),
    });
  };

  render() {
    let index = this.props.arr.findIndex((elem) => elem.name === "Size");
    console.log(this.state.sizes);
    return (
      <>
        <div className="size">
          <div className="size__title">SIZE:</div>
          <div className="size__container">
            {this.props.arr[index].items.map((item) => {
              return (
                <div
                  className={`size__box ${
                    item.isActive ? "size__box active" : ""
                  }`}
                  // className="size__box"
                  key={item.id}
                  id={item.id}
                  onClick={(e) => {
                    this.props.setSize(e.target.id);
                  }}
                  onClick={this.addClass}
                >
                  <div>{item.value}</div>
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Size;
