import React from "react";
import { Component } from "react";
import "./Size.scss";

class Size extends Component {
  active() {
    return this.props.activeSize === this.props.arr.id;
  }

  render() {
    let index = this.props.arr.findIndex((elem) => elem.name === "Size");
    return (
      <>
        <div className="size">
          <div className="size__title">SIZE:</div>
          <div className="size__container">
            {this.props.arr[index].items.map((item) => {
              return (
                <div
                  className={` ${
                    this.active ? "size__box active" : "size__box"
                  }`}
                  // className="size__box"
                  key={item.id}
                  id={item.id}
                  onClick={(e) => {
                    this.props.setSize(e.target.id);
                  }}
                >
                  <div>{item.value}</div>
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default Size;
